mapper.select:
select t1.id,t1.user_name,t1.password,t1.role_id,t1.del_flag,t1.crt_by,t1.crt_time,t1.upd_by,t1.upd_time,t2.role_name
from sys_user t1 left join sys_role t2 on t1.role_id = t2.id
where t1.del_flag != '1'

    AND t1.id= #{id}


    AND t1.user_name like CONCAT('%',#{userName},'%')


    AND t1.role_id = #{roleId}

select

from sys_user
where user_name = #{userName,jdbcType=VARCHAR}
and password = #{password,jdbcType=VARCHAR}
and del_flag !=1
